import React, { useState, useEffect } from 'react';
import { Play, Pause, RotateCcw } from 'lucide-react';

const Pomodoro = () => {
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [mode, setMode] = useState('work'); // 'work' or 'break'

  useEffect(() => {
    let interval = null;

    if (isActive) {
      interval = setInterval(() => {
        if (seconds > 0) {
          setSeconds(seconds - 1);
        } else if (minutes > 0) {
          setMinutes(minutes - 1);
          setSeconds(59);
        } else {
          // Timer completed
          clearInterval(interval);
          const notification = new Audio('data:audio/wav;base64,');
          notification.play();
          
          // Switch modes
          if (mode === 'work') {
            setMode('break');
            setMinutes(5);
          } else {
            setMode('work');
            setMinutes(25);
          }
          setSeconds(0);
          setIsActive(false);
        }
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isActive, minutes, seconds, mode]);

  const toggleTimer = () => {
    setIsActive(!isActive);
  };

  const resetTimer = () => {
    setIsActive(false);
    if (mode === 'work') {
      setMinutes(25);
    } else {
      setMinutes(5);
    }
    setSeconds(0);
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-black">
      <div className="bg-white p-8 rounded-lg shadow-lg text-center">
        <h1 className="text-3xl font-bold mb-8">
          {mode === 'work' ? 'Work Time' : 'Break Time'}
        </h1>
        
        <div className="text-6xl font-bold mb-8 text-gray-800">
          {String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
        </div>
        
        <div className="flex gap-4 justify-center">
          <button
            onClick={toggleTimer}
            className="bg-blue-500 hover:bg-blue-600 text-white p-4 rounded-full"
          >
            {isActive ? <Pause className="h-6 w-6" /> : <Play className="h-6 w-6" />}
          </button>
          <button
            onClick={resetTimer}
            className="bg-gray-500 hover:bg-gray-600 text-white p-4 rounded-full"
          >
            <RotateCcw className="h-6 w-6" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Pomodoro;